queryDetail.value:
  , PUB_pm_staff_data
WHERE  PUB_p_partner.p_partner_key_n = PUB_pm_staff_data.p_partner_key_n
  AND PUB_pm_staff_data.pm_start_of_commitment_d <= {#param_currentstaffdate#}
  AND ( PUB_pm_staff_data.pm_end_of_commitment_d >= {#param_currentstaffdate#}
    OR PUB.pm_staff_data.pm_end_of_commitment_d IS NULL )
